import streamlit as stimport pandas as pdimport joblib# Load the trained modelmodel = joblib.load("xgb_cholera_model.pkl")st.title("Cholera Outbreak Risk Prediction")# Input formrainfall = st.number_input("Rainfall (mm)", min_value=0)temperature = st.number_input("Temperature (Â°C)", min_value=0)humidity = st.number_input("Humidity (%)", min_value=0, max_value=100)water_contamination = st.slider("Water Contamination Level", 0.0, 1.0, 0.1)sanitation_coverage = st.slider("Sanitation Coverage", 0.0, 1.0, 0.5)population_density = st.number_input("Population Density", min_value=0)reported_cases = st.number_input("Reported Cholera Cases", min_value=0)# Predict buttonif st.button("Predict"):    input_data = pd.DataFrame({        'rainfall': [rainfall],        'temperature': [temperature],        'humidity': [humidity],        'water_contamination': [water_contamination],        'sanitation_coverage': [sanitation_coverage],        'population_density': [population_density],        'reported_cases': [reported_cases]    })    prediction = model.predict(input_data)    result = "High Risk of Cholera Outbreak" if prediction[0] == 1 else "Low Risk of Cholera Outbreak"    st.subheader("Prediction Result:")    st.success(result)